export function add24(a: number, b: number): number {
  return a + b;
}

export function subtract24(a: number, b: number): number {
  return a - b;
}

export function multiply24(a: number, b: number): number {
  return a * b;
}

export function divide24(a: number, b: number): number {
  return a / b;
}
